import React, { useState } from 'react';

function App() {
  const [step, setStep] = useState(1);
  const [clientData, setClientData] = useState({
    name: '',
    email: '',
    contactNumber: '',
    taxId: '',
    idType: 'cedula',
  });
  const [vehicleData, setVehicleData] = useState({
    brand: '',
    model: '',
    plate: '',
    fuelLevel: '',
    vehicleCondition: '',
  });
  const [selectedServices, setSelectedServices] = useState([]);

  const handleNextStep = () => {
    setStep((prevStep) => prevStep + 1);
  };

  const handlePreviousStep = () => {
    setStep((prevStep) => prevStep - 1);
  };

  const handleClientDataChange = (e) => {
    setClientData((prevData) => ({
      ...prevData,
      [e.target.name]: e.target.value,
    }));
  };

  const handleVehicleDataChange = (e) => {
    setVehicleData((prevData) => ({
      ...prevData,
      [e.target.name]: e.target.value,
    }));
  };

  const handleServiceSelection = (service) => {
    setSelectedServices((prevServices) => [...prevServices, service]);
  };

  const handleServiceDeselection = (service) => {
    setSelectedServices((prevServices) =>
      prevServices.filter((s) => s !== service)
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Aquí puedes realizar la lógica para generar la orden de trabajo y enviarla
    // a un servidor o almacenarla en algún lugar.
    console.log('Orden de trabajo generada:', {
      clientData,
      vehicleData,
      selectedServices,
    });
  };

  // Renderizado condicional de los pasos
  let stepContent;
  switch (step) {
    case 1:
      stepContent = (
        <div>
          <h2>Datos del cliente</h2>
          <form>
            <label>Nombre del cliente:</label>
            <input
              type="text"
              name="name"
              value={clientData.name}
              onChange={handleClientDataChange}
              required
            />
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={clientData.email}
              onChange={handleClientDataChange}
              required
            />
            <label>Número de contacto:</label>
            <input
              type="text"
              name="contactNumber"
              value={clientData.contactNumber}
              onChange={handleClientDataChange}
              required
            />
            <label>Identificación fiscal:</label>
            <input
              type="text"
              name="taxId"
              value={clientData.taxId}
              onChange={handleClientDataChange}
              required
            />
            <label>Tipo de identificación:</label>
            <select
              name="idType"
              value={clientData.idType}
              onChange={handleClientDataChange}
              required
            >
              <option value="cedula">Cédula</option>
              <option value="ruc">RUC</option>
              <option value="pasaporte">Pasaporte</option>
            </select>
            <button type="button" onClick={handleNextStep}>
              Siguiente
            </button>
          </form>
        </div>
      );
      break;
    case 2:
      stepContent = (
        <div>
          <h2>Datos del vehículo</h2>
          <form>
            <label>Marca:</label>
            <input
              type="text"
              name="brand"
              value={vehicleData.brand}
              onChange={handleVehicleDataChange}
              required
            />
            <label>Modelo:</label>
            <input
              type="text"
              name="model"
              value={vehicleData.model}
              onChange={handleVehicleDataChange}
              required
            />
            <label>Placa:</label>
            <input
              type="text"
              name="plate"
              value={vehicleData.plate}
              onChange={handleVehicleDataChange}
              required
            />
            <label>Nivel del tanque de gasolina:</label>
            <input
              type="text"
              name="fuelLevel"
              value={vehicleData.fuelLevel}
              onChange={handleVehicleDataChange}
              required
            />
            <label>Estado exterior del vehículo:</label>
            <textarea
              name="vehicleCondition"
              value={vehicleData.vehicleCondition}
              onChange={handleVehicleDataChange}
            ></textarea>
            <button type="button" onClick={handlePreviousStep}>
              Anterior
            </button>
            <button type="button" onClick={handleNextStep}>
              Siguiente
            </button>
          </form>
        </div>
      );
      break;
    case 3:
      stepContent = (
        <div>
          <h2>Selección de servicios</h2>
          <ul>
            <li>
              <label>
                <input
                  type="checkbox"
                  checked={selectedServices.includes('cambio-aceite')}
                  onChange={() =>
                    selectedServices.includes('cambio-aceite')
                      ? handleServiceDeselection('cambio-aceite')
                      : handleServiceSelection('cambio-aceite')
                  }
                />
                Cambio de aceite
              </label>
            </li>
            <li>
              <label>
                <input
                  type="checkbox"
                  checked={selectedServices.includes('cambio-frenos')}
                  onChange={() =>
                    selectedServices.includes('cambio-frenos')
                      ? handleServiceDeselection('cambio-frenos')
                      : handleServiceSelection('cambio-frenos')
                  }
                />
                Cambio de frenos
              </label>
            </li>
            <li>
              <label>
                <input
                  type="checkbox"
                  checked={selectedServices.includes('alineacion-balanceo')}
                  onChange={() =>
                    selectedServices.includes('alineacion-balanceo')
                      ? handleServiceDeselection('alineacion-balanceo')
                      : handleServiceSelection('alineacion-balanceo')
                  }
                />
                Alineación y balanceo
              </label>
            </li>
            <li>
              <label>
                <input
                  type="checkbox"
                  checked={selectedServices.includes('diagnostico-general')}
                  onChange={() =>
                    selectedServices.includes('diagnostico-general')
                      ? handleServiceDeselection('diagnostico-general')
                      : handleServiceSelection('diagnostico-general')
                  }
                />
                Diagnóstico general
              </label>
            </li>
            <li>
              <label>
                <input
                  type="checkbox"
                  checked={selectedServices.includes('revision-electrica')}
                  onChange={() =>
                    selectedServices.includes('revision-electrica')
                      ? handleServiceDeselection('revision-electrica')
                      : handleServiceSelection('revision-electrica')
                  }
                />
                Revisión del sistema eléctrico
              </label>
            </li>
            <li>
              <label>
                <input
                  type="checkbox"
                  checked={selectedServices.includes('revision-suspension')}
                  onChange={() =>
                    selectedServices.includes('revision-suspension')
                      ? handleServiceDeselection('revision-suspension')
                      : handleServiceSelection('revision-suspension')
                  }
                />
                Revisión de la suspensión
              </label>
            </li>
          </ul>
          <button type="button" onClick={handlePreviousStep}>
            Anterior
          </button>
          <button type="button" onClick={handleNextStep}>
            Siguiente
          </button>
        </div>
      );
      break;
    case 4:
      stepContent = (
        <div>
          <h2>Orden de trabajo</h2>
          <h3>Datos del cliente:</h3>
          <p>Nombre: {clientData.name}</p>
          <p>Email: {clientData.email}</p>
          <p>Número de contacto: {clientData.contactNumber}</p>
          <p>Identificación fiscal: {clientData.taxId}</p>
          <p>Tipo de identificación: {clientData.idType}</p>
          <h3>Datos del vehículo:</h3>
          <p>Marca: {vehicleData.brand}</p>
          <p>Modelo: {vehicleData.model}</p>
          <p>Placa: {vehicleData.plate}</p>
          <p>Nivel del tanque de gasolina: {vehicleData.fuelLevel}</p>
          <p>Estado exterior del vehículo: {vehicleData.vehicleCondition}</p>
          <h3>Servicios seleccionados:</h3>
          <ul>
            {selectedServices.map((service) => (
              <li key={service}>{service}</li>
            ))}
          </ul>
          <button type="button" onClick={handlePreviousStep}>
            Anterior
          </button>
          <button type="submit">Generar Orden de Trabajo</button>
        </div>
      );
      break;
    default:
      break;
  }

  return (
    <div className="App">
      <h1>Recepción de Autos en Taller Automotriz</h1>
      <form onSubmit={handleSubmit}>
        {stepContent}
      </form>
    </div>
  );
}

export default App;
