import React, { useState } from 'react';
import ClientForm from './ClientForm';
import VehicleForm from './VehicleForm';
import ServiceSelection from './ServiceSelection';
import WorkOrder from './WorkOrder';

function App() {
  const [step, setStep] = useState(1);
  const [clientData, setClientData] = useState({});
  const [vehicleData, setVehicleData] = useState({});
  const [selectedServices, setSelectedServices] = useState([]);
  const [estimatedDelivery, setEstimatedDelivery] = useState('');

  const handleClientFormSubmit = (data) => {
    setClientData(data);
    setStep(2);
  };

  const handleVehicleFormSubmit = (data) => {
    setVehicleData(data);
    setStep(3);
  };

  const handleServiceSelectionSubmit = (services) => {
    setSelectedServices(services);
    setStep(4);
    setEstimatedDelivery(generateEstimatedDelivery());
  };

  const generateEstimatedDelivery = () => {
    // Lógica para generar la fecha y hora estimada de entrega
    return "Fecha y hora estimada de entrega";
  };

  const handleWorkOrderApprove = () => {
    // Lógica para manejar la aprobación final de la orden de trabajo
  };

  return (
    <div className="App">
      {step === 1 && <ClientForm onSubmit={handleClientFormSubmit} />}
      {step === 2 && <VehicleForm onSubmit={handleVehicleFormSubmit} />}
      {step === 3 && <ServiceSelection onSubmit={handleServiceSelectionSubmit} />}
      {step === 4 && (
        <WorkOrder
          clientData={clientData}
          vehicleData={vehicleData}
          selectedServices={selectedServices}
          estimatedDelivery={estimatedDelivery}
          onApprove={handleWorkOrderApprove}
        />
      )}
    </div>
  );
}

export default App;
